package org.bjb;import java.awt.*;import java.awt.event.*;import javax.swing.*;public class InfoOver extends Canvas implements ActionListener {	ImageIcon infoImg = null;	private boolean active = false;	private StatusArea statusArea = null;		public InfoOver() {		infoImg = Card.getIcon("images/info.jpg");		Dimension d = new Dimension(infoImg.getIconWidth(),infoImg.getIconHeight());		setSize(d);		setMinimumSize(d);		setMaximumSize(d);		setBackground(BlackJackApp.teal);		final InfoOver io = this;		addMouseListener(new java.awt.event.MouseAdapter() {  			public void mouseEntered(java.awt.event.MouseEvent evt) {   				active = true;  				statusArea = new StatusArea();  				statusArea.pack();				javax.swing.Timer t = new javax.swing.Timer(500,io);				t.setRepeats(false);				t.start();	  			}			public void mouseExited(java.awt.event.MouseEvent evt) { 				statusArea.setVisible(false);					active = false; 			}		});	}	public void actionPerformed(final ActionEvent evt) {		String cmd = evt.getActionCommand();		if (cmd == null) {			if (active) {/* 				BlackJackApp.currCard.setText(new Integer(BlackJackGame.deck.topCard).toString());				if (BlackJackGame.deck.cutCard == 0) BlackJackGame.deck.checkShuffle();  				BlackJackApp.cutCard.setText(new Integer(BlackJackGame.deck.cutCard).toString());  				BlackJackApp.roundNum.setText(new Integer(BlackJackGame.rounds).toString());  				BlackJackGame.updateElapsed();		// ensure current  				BlackJackApp.elapsedTime.setText(BlackJackGame.elapsedTime);		*/					statusArea.setVisible(true);			}		}	}			public void paint(Graphics g) {		super.paint(g);		infoImg.paintIcon(this,g,0,0);	}}